<?php

use app\Utils\Base64;
use PHPUnit\Framework\TestCase;

class Base64Test extends TestCase
{
    public function testGetDataForImageFile()
    {
        $this->assertSame(
            "data:image/png;base64,",
            Base64::getDataForImageFile(DIR_BASE . '/public/static/bsassets/MicDrop.png'),
            'getDataForImageFile() should correctly convert image files to base64 data: URIs'
        );

        $this->assertNull(Base64::getDataForImageFile('invalid_image_path'),
            'getDataForImageFile() should quietly return null for invalid paths');
        $this->assertNull(Base64::getDataForImageFile(DIR_BASE . '/bootstrap.php'),
            'getDataForImageFile() should quietly return null for non-image files');
    }
}
